<?php header('Content-type: text/css');if (!preg_match("/^([a-zA-Z0-9_\-]+.css$)/", $_GET['file']))  {	die('Not CSS');}if(file_exists($_GET['file'])) {$sText = file_get_contents($_GET['file']) or die('Could not open file.');$sText = preg_replace('!/\*[^*]*\*+([^/][^*]*\*+)*/!', '', $sText);$sHash = md5($sText);header("Last-Modified: ".date("D, d M Y H:i:s T", filemtime($_GET['file'])));header("ETag: \"{$sHash}\"");	echo str_replace(array("\r\n", "\r", "\n", "\t", '  ', '   ', '    ', '     '), '', $sText);}else {	echo 'File Not Found';}?>
